INSERT INTO `page_lang` (`id`, `page_id`, `language`, `title`, `content`) VALUES
(3, 3, 'en-US', 'About', '<section class=\"aboutpg-section\">\r\n<div class=\"container\">\r\n<div class=\"titlesection\">\r\n<h2 class=\"maintitle\">A Professional Tourism Corporation Based in Jeddah, Saudi Arabia.</h2>\r\n</div>\r\n<div class=\"detail\">\r\n<h6 class=\"title\">It is licensed by Ministry of Commerce, Saudi travel and tourism association and the Saudi Commission for Tourism and National Heritage.</h6>\r\n<p class=\"description\">We aim to be a unique tourism services provider through helping people to travel smart within their budget. Our mission is to combine our travel knowledge and expertise, enabling our customers to plan and then enjoy an amazing travel experience through providing them with fair value against requested tourism services and full commitments of our promises.</p>\r\n</div>\r\n</div>\r\n</section>\r\n<section class=\"vision-mission-section\">\r\n<div class=\"container\">\r\n<article>\r\n<div class=\"row\">\r\n<div class=\"col-md-6 col-nopadd\">\r\n<figure class=\"matchheight\"><img src=\"http://rawnaq.grassflorist.com/uploads/2018/08/about-img1.jpg\" alt=\"\" /></figure>\r\n</div>\r\n<div class=\"col-md-6 col-nopadd\">\r\n<div class=\"content matchheight\">\r\n<h4 class=\"title\">Vision</h4>\r\n<h6 class=\"desc\">Rawnaq aims to be a unique tourism services provider through helping people to travel smart within their budget.</h6>\r\n</div>\r\n</div>\r\n</div>\r\n</article>\r\n<article>\r\n<div class=\"row\">\r\n<div class=\"col-md-6 col-nopadd\">\r\n<div class=\"content matchheight\">\r\n<h4 class=\"title\">Mission</h4>\r\n<h6 class=\"desc\">Enabling our customers to plan and enjoy an amazing travel experience with our vast experience and expertise .</h6>\r\n</div>\r\n</div>\r\n<div class=\"col-md-6 col-nopadd\">\r\n<figure class=\"matchheight\"><img src=\"http://rawnaq.grassflorist.com/uploads/2018/08/about-img2.jpg\" alt=\"\" /></figure>\r\n</div>\r\n</div>\r\n</article>\r\n</div>\r\n</section>\r\n<section class=\"about-section\">\r\n<div class=\"container\">\r\n<h6 class=\"title\">At Rawnaq you can find a variety of specialized tourism services with just one stop such as Ticketing, Hotels Booking and Tourism Packages for the luxury destinations all over the world.</h6>\r\n<p class=\"description\">This is allowed us to be one of the most admired and accepted corporations in the tourism field in GCC, serving more than 500,000 followers in our social media channels and numerous corporate clients as well. With our proven history of commitments, honesty, responsibility, efficiency and problem-solving skills, we are well suited to fulfill the unique requirements our clients. We highly appreciate our strong team members and their cooperation for the success of our corporation.</p>\r\n</div>\r\n</section>\r\n<section class=\"values-section\">\r\n<div class=\"container\">\r\n<h2 class=\"title\">Values</h2>\r\n<ul class=\"unstyled values-detail grid-block --type-two-blocks\">\r\n<li>Honesty</li>\r\n<li>Quality</li>\r\n<li>Innovation</li>\r\n<li>Efficiency</li>\r\n<li>Responsibility</li>\r\n<li>Commitments</li>\r\n</ul>\r\n</div>\r\n</section>'),
(4, 4, 'en-US', 'Destinations', '<h2 class=\"maintitle\">Popular Tourist Destinations Around The Globe</h2>\r\n<p class=\"subtitle\">Explore Your Favorite Destination</p>'),
(5, 5, 'en-US', 'Bank Details', '<section class=\"destination-section\">\r\n<div class=\"container\">\r\n<div class=\"row\">\r\n<div class=\"col-md-5 offset-md-1 bank-details\"><img class=\"d-inline\" src=\"http://nebula.wsimg.com/cb8fbc3e755275a77a584c56016bf10b?AccessKeyId=0A1BEB9D109D2B8291AC&amp;disposition=0&amp;alloworigin=1\" alt=\"*\" />\r\n<p>Bank Name: National Commercial Bank<br /> Account Name: International Rawnaq Est<br /> Swift Code: ‏NCBKSAJE<br /> Account Number: 12461838000102<br /> IBAN Number: ‏SA1410000012461838000102</p>\r\n</div>\r\n<div class=\"col-md-5 bank-details borderspc-left\"><img class=\"d-inline\" src=\"http://nebula.wsimg.com/5bf0e29612cc04c9e9d5f492d3cbd083?AccessKeyId=0A1BEB9D109D2B8291AC&amp;disposition=0&amp;alloworigin=1\" alt=\"*\" />\r\n<p>Bank Name: Riyad Bank<br /> Account Name: Rawnaq International Travel and Tourism<br /> Swift Code: ‏RIBLSARI<br /> Account Number: 1170621089940<br /> IBAN Number : ‏SA8820000001170621089940</p>\r\n</div>\r\n</div>\r\n<div class=\"row\">\r\n<div class=\"col-md-5 offset-md-1 bank-details\"><img class=\"d-inline\" src=\"http://rawnaq.grassflorist.com/uploads/2018/10/aljazirabank.png\" alt=\"aljazirabank.png\" width=\"314\" height=\"70\" />\r\n<p>Bank Name: Aljazira Bank<br /> Account Name: Rawnaq International Travel and Tourism<br /> Swift Code: BJAZSAJE<br /> Account Number: 002295006736001<br /> IBAN Number: SA0960100002295006736001</p>\r\n</div>\r\n<div class=\"col-md-5 bank-details borderspc-left\"><img class=\"d-inline\" src=\"http://rawnaq.grassflorist.com/uploads/2018/10/sambabank.png\" alt=\"sambabank.png\" width=\"257\" height=\"70\" />\r\n<p>Bank Name: Samba Bank<br /> Account Name: Rawnaq International Travel and Tourism<br /> Swift Code: SAMBSARI<br /> Account Number: 125015429<br /> IBAN Number : ‏SA740000000000125015429</p>\r\n</div>\r\n</div>\r\n</div>\r\n</section>'),
